tables.table_schema:
CREATE TABLE product_bundles (
  customer_id INT,
  product_id INT,
  quantity INT
);

CREATE TABLE customers (
    customer_id INT PRIMARY KEY,
    customer_name VARCHAR(255),
    email VARCHAR(255),
    age INT
);

CREATE TABLE product_bundles (
    bundle_id INT PRIMARY KEY,
    bundle_name VARCHAR(255),
    products JSONB
);
