tables.table_schema:
CREATE TABLE customer_feedback (
    feedback_id INT PRIMARY KEY,
    customer_id INT,
    feedback_text TEXT,
    timestamp TIMESTAMP
);

CREATE TABLE service_improvement (
    improvement_id INT PRIMARY KEY,
    feedback_id INT,
    recommendation TEXT,
    implemented BOOLEAN
);

CREATE TABLE customer_feedback (
  feedback_id UUID PRIMARY KEY,
  customer_id UUID NOT NULL,
  feedback_text TEXT NOT NULL,
  feedback_date TIMESTAMP NOT NULL
);

CREATE TABLE service_improvement (
  improvement_id UUID PRIMARY KEY,
  feedback_id UUID REFERENCES customer_feedback(feedback_id),
  improvement_description TEXT NOT NULL,
  improvement_date TIMESTAMP NOT NULL
);

CREATE TABLE CustomerFeedback (
  feedback_id INT PRIMARY KEY,
  customer_name VARCHAR(50),
  feedback_text TEXT,
  rating INT
);

CREATE TABLE FeedbackCategories (
  category_id INT PRIMARY KEY,
  category_name VARCHAR(50)
);

CREATE TABLE FeedbackRatings (
  rating_id INT PRIMARY KEY,
  rating_value INT
);
