CREATE TABLE landfill_sites (
    site_id INT PRIMARY KEY,
    site_name VARCHAR(255) NOT NULL,
    latitude DECIMAL NOT NULL,
    longitude DECIMAL NOT NULL
);

CREATE TABLE landfill_coordinates (
    coordinate_id INT PRIMARY KEY,
    site_id INT REFERENCES landfill_sites(site_id),
    coordinate_name VARCHAR(255) NOT NULL,
    latitude DECIMAL NOT NULL,
    longitude DECIMAL NOT NULL
);

CREATE TABLE site_activity (
    activity_id INT PRIMARY KEY,
    site_id INT REFERENCES landfill_sites(site_id),
    activity_type VARCHAR(100) NOT NULL,
    timestamp TIMESTAMP
);

CREATE TABLE landfill_sites (
    site_id INT PRIMARY KEY,
    site_name TEXT,
    location_geom GEOMETRY,
    coordinates TEXT
);

CREATE TABLE landfill_sites (
    site_id INT PRIMARY KEY,
    site_name VARCHAR(255) NOT NULL,
    site_location VARCHAR(255) NOT NULL,
    longitude DECIMAL(10,6) NOT NULL,
    latitude DECIMAL(10,6) NOT NULL
);

CREATE TABLE landfill_site_categories (
    category_id INT PRIMARY KEY,
    category_name VARCHAR(255) NOT NULL
);

CREATE TABLE site_managers (
    manager_id INT PRIMARY KEY,
    manager_name VARCHAR(255) NOT NULL
);

CREATE TABLE waste_types (
    waste_type_id INT PRIMARY KEY,
    waste_type_name VARCHAR(255) NOT NULL
);

CREATE TABLE waste_disposal_records (
    record_id INT PRIMARY KEY,
    site_id INT NOT NULL,
    manager_id INT NOT NULL,
    waste_type_id INT NOT NULL,
    quantity DECIMAL(10,2) NOT NULL,
    disposal_date DATE NOT NULL
);
