tables.table_schema:
CREATE TABLE waste_permits (
  permit_id INT PRIMARY KEY,
  company_name VARCHAR(255),
  permit_issued_date DATE
);

CREATE TABLE waste_companies (
  company_id INT PRIMARY KEY,
  company_name VARCHAR(255),
  location VARCHAR(255),
  permit_id INT REFERENCES waste_permits(permit_id)
);

CREATE TABLE WastePermits (
  permit_id UUID PRIMARY KEY,
  company_name VARCHAR(255) NOT NULL,
  permit_type VARCHAR(50) NOT NULL,
  issue_date DATE NOT NULL
);

CREATE TABLE WasteCompanies (
  company_id UUID PRIMARY KEY,
  company_name VARCHAR(255) NOT NULL,
  address VARCHAR(255) NOT NULL,
  contact_number VARCHAR(20) NOT NULL
);

CREATE TABLE PermitHistory (
  history_id UUID PRIMARY KEY,
  permit_id UUID NOT NULL,
  issue_date DATE NOT NULL,
  status VARCHAR(50) NOT NULL
);

CREATE TABLE RegulatoryAuthorities (
  authority_id UUID PRIMARY KEY,
  authority_name VARCHAR(100) NOT NULL,
  region VARCHAR(50) NOT NULL,
  contact_email VARCHAR(100) NOT NULL
);

CREATE TABLE permit_documents (
    document_id INT PRIMARY KEY,
    permit_id INT NOT NULL,
    document_type VARCHAR(50) NOT NULL,
    document_url TEXT NOT NULL
);
