tables.table_schema:
CREATE TABLE DailyWasteIntake (
  date DATE,
  waste_type VARCHAR,
  waste_weight DECIMAL,
  composition VARCHAR
);

CREATE TABLE daily_waste_intake (
    id UUID PRIMARY KEY,
    date DATE,
    waste_type VARCHAR,
    quantity DECIMAL
);

CREATE TABLE waste_composition (
    id UUID PRIMARY KEY,
    composition VARCHAR,
    impact_rating INTEGER
);

CREATE TABLE composition_categories (
    category_id SERIAL PRIMARY KEY,
    category_name VARCHAR(50) NOT NULL
);

CREATE TABLE waste_intake_records (
    record_id SERIAL PRIMARY KEY,
    date DATE REFERENCES daily_waste_intake(date),
    category_id INT REFERENCES composition_categories(category_id),
    weight DECIMAL
);

CREATE TABLE environmental_impact_assessment (
    assessment_id SERIAL PRIMARY KEY,
    record_id INT REFERENCES waste_intake_records(record_id),
    impact_score FLOAT,
    assessment_date DATE
);
