tables.table_schema:
CREATE TABLE landfill_inspection_reports (
  report_id INT PRIMARY KEY,
  report_date DATE,
  inspector_name VARCHAR(50),
  compliance_status VARCHAR(20)
);

CREATE TABLE inspection_details (
  detail_id INT PRIMARY KEY,
  report_id INT REFERENCES landfill_inspection_reports(report_id),
  inspection_date DATE,
  inspector_notes TEXT
);

CREATE TABLE inspection_photos (
    photo_id INT PRIMARY KEY,
    finding_id INT,
    photo_url VARCHAR(255),
    CONSTRAINT fk_finding_id FOREIGN KEY (finding_id) REFERENCES inspection_findings(finding_id)
);

CREATE TABLE inspection_photos (
   photo_id SERIAL PRIMARY KEY,
   inspection_id INT,
   photo_url TEXT,
   photo_description TEXT
);
