CREATE TABLE waste_composition (
  id INT PRIMARY KEY,
  waste_type VARCHAR,
  compostable BOOLEAN,
  recyclable BOOLEAN
);

CREATE TABLE landfill_capacity (
  id INT PRIMARY KEY,
  landfill_id INT,
  capacity INT,
  CONSTRAINT fk_landfill_id FOREIGN KEY (landfill_id) REFERENCES waste_composition(id)
);

CREATE TABLE expansion_projects (
  id INT PRIMARY KEY,
  project_name VARCHAR,
  start_date DATE,
  end_date DATE
);

CREATE TABLE forecasting_data (
  id INT PRIMARY KEY,
  landfill_id INT,
  prediction_date DATE,
  waste_type VARCHAR,
  prediction_amount INT,
  CONSTRAINT fk_landfill_id FOREIGN KEY (landfill_id) REFERENCES landfill_capacity(id)
);

CREATE TABLE users (
  id INT PRIMARY KEY,
  username VARCHAR,
  email VARCHAR
);
