tables.table_schema:
CREATE TABLE landfill_emissions (
    id UUID PRIMARY KEY,
    date DATE,
    gas_type VARCHAR,
    emission_level DECIMAL
);

CREATE TABLE mitigation_strategies (
    id UUID PRIMARY KEY,
    strategy_name VARCHAR,
    description TEXT
);

CREATE TABLE monitoring_equipment (
    id UUID PRIMARY KEY,
    equipment_name VARCHAR,
    brand VARCHAR,
    model VARCHAR
);

CREATE TABLE gas_emission_readings (
    id UUID PRIMARY KEY,
    emission_id UUID,
    reading_time TIMESTAMP,
    value DECIMAL
);
