tables.table_schema:
CREATE TABLE landfill_closure (
  id INT PRIMARY KEY,
  closure_date DATE,
  closure_method VARCHAR(50),
  monitoring_frequency INT
);
CREATE TABLE post_closure_activities (
  id INT PRIMARY KEY,
  activity_date DATE,
  activity_type VARCHAR(50),
  responsible_party VARCHAR(100)
);

CREATE TABLE landfill_closure_activities (
    activity_id INT PRIMARY KEY,
    activity_date DATE,
    activity_type VARCHAR,
    activity_description TEXT
);

CREATE TABLE post_closure_activities (
    post_activity_id INT PRIMARY KEY,
    post_activity_date DATE,
    post_activity_type VARCHAR,
    post_activity_description TEXT
);

CREATE TABLE environmental_monitoring (
    monitoring_id INT PRIMARY KEY,
    monitoring_date DATE,
    monitoring_type VARCHAR,
    monitoring_results TEXT
);

CREATE TABLE landfill_sites (
  site_id SERIAL PRIMARY KEY,
  site_name TEXT NOT NULL,
  closure_date DATE,
  post_closure_plan TEXT
);

CREATE TABLE landfill_closure_activities (
  activity_id SERIAL PRIMARY KEY,
  site_id INTEGER REFERENCES landfill_sites (site_id),
  activity_description TEXT,
  activity_date DATE
);

CREATE TABLE monitoring_data (
  data_id SERIAL PRIMARY KEY,
  site_id INTEGER REFERENCES landfill_sites (site_id),
  monitoring_date DATE,
  monitoring_results TEXT
);

CREATE TABLE remediation_activities (
  remediation_id SERIAL PRIMARY KEY,
  site_id INTEGER REFERENCES landfill_sites (site_id),
  remediation_description TEXT,
  remediation_date DATE
);
