tables.table_schema:
CREATE TABLE recycling_rates (
  bin_id INT,
  date DATE,
  recycling_rate DECIMAL
);

CREATE TABLE recycling_bin_data (
  bin_id INT,
  location VARCHAR(255),
  recycling_rate DECIMAL,
  collection_date DATE
);

CREATE TABLE recycling_trends (
  trend_id INT,
  year INT,
  trend_description TEXT,
  trend_value DECIMAL
);

CREATE TABLE recycling_centers (
    center_id INT PRIMARY KEY,
    location VARCHAR(255),
    contact_person VARCHAR(50),
    phone_number VARCHAR(15)
);
