tables.table_schema:
CREATE TABLE compost_materials (
  id UUID PRIMARY KEY,
  material_name VARCHAR(255) NOT NULL,
  quantity DECIMAL NOT NULL,
  compost_date TIMESTAMP NOT NULL
);

CREATE TABLE compost_materials (
  id INT PRIMARY KEY,
  material_name VARCHAR(255),
  quantity DECIMAL(10,2)
);

CREATE TABLE compost_bins (
  bin_id INT PRIMARY KEY,
  bin_location VARCHAR(255),
  material_id INT,
  FOREIGN KEY (material_id) REFERENCES compost_materials(id)
);

CREATE TABLE compost_materials (
  material_id INT PRIMARY KEY,
  material_name VARCHAR(50) NOT NULL,
  material_type VARCHAR(50) NOT NULL,
  quantity DECIMAL(10,2) NOT NULL
);

CREATE TABLE compost_bins (
  bin_id INT PRIMARY KEY,
  bin_name VARCHAR(50) NOT NULL,
  capacity DECIMAL(10,2) NOT NULL
);

CREATE TABLE CompostMaterials (
  material_id INT PRIMARY KEY,
  material_name VARCHAR(50),
  material_type VARCHAR(50),
  quantity DECIMAL
);

CREATE TABLE CompostBins (
  bin_id INT PRIMARY KEY,
  bin_location VARCHAR(100),
  bin_capacity DECIMAL
);

CREATE TABLE CompostUsers (
  user_id INT PRIMARY KEY,
  username VARCHAR(50),
  email VARCHAR(100)
);

CREATE TABLE compost_materials (
  id INT PRIMARY KEY,
  material_name VARCHAR(255),
  carbon_content DECIMAL,
  nitrogen_content DECIMAL
);

CREATE TABLE compost_bins (
  bin_id INT PRIMARY KEY,
  bin_capacity DECIMAL,
  current_fill_level DECIMAL,
  material_id INT references compost_materials(id)
);

CREATE TABLE compost_sites (
  site_id INT PRIMARY KEY,
  site_name VARCHAR(255),
  location VARCHAR(255)
);

CREATE TABLE compost_users (
  user_id INT PRIMARY KEY,
  username VARCHAR(255),
  email VARCHAR(255)
);

CREATE TABLE compost_logs (
  log_id INT PRIMARY KEY,
  activity VARCHAR(255),
  user_id INT references compost_users(user_id)
);
