tables.table_schema:
CREATE TABLE TemperatureData (
  date TIMESTAMP,
  temperature FLOAT,
  humidity FLOAT
);

CREATE TABLE Temperature_Readings (
    reading_id INT PRIMARY KEY,
    temperature DECIMAL,
    timestamp TIMESTAMP
);

CREATE TABLE Compost_Status (
    compost_id INT PRIMARY KEY,
    compost_temperature DECIMAL,
    status VARCHAR,
    last_update TIMESTAMP
);

CREATE TABLE TemperatureData (
  id SERIAL PRIMARY KEY,
  timestamp TIMESTAMP NOT NULL,
  temperature FLOAT NOT NULL
);

CREATE TABLE CompostingBins (
  bin_id SERIAL PRIMARY KEY,
  location TEXT NOT NULL,
  capacity INTEGER NOT NULL
);

CREATE TABLE ProcessMonitoring (
  monitoring_id SERIAL PRIMARY KEY,
  bin_id INTEGER NOT NULL,
  timestamp TIMESTAMP NOT NULL,
  status TEXT NOT NULL
);

CREATE TABLE composting_site (
  site_id INT PRIMARY KEY,
  site_name TEXT,
  location TEXT
);

CREATE TABLE temperature_sensor (
  sensor_id INT PRIMARY KEY,
  sensor_name TEXT,
  site_id INT,
  FOREIGN KEY (site_id) REFERENCES composting_site(site_id)
);
