tables.table_schema:
CREATE TABLE compost_turning_dates (
  batch_id INT PRIMARY KEY,
  turn_date DATE,
  mix_date DATE
);

CREATE TABLE compost_batches (
    batch_id INT PRIMARY KEY,
    turn_date DATE,
    mix_date DATE
);

CREATE TABLE compost_log (
    log_id INT PRIMARY KEY,
    batch_id INT,
    action VARCHAR(50),
    log_date TIMESTAMP,
    FOREIGN KEY (batch_id) REFERENCES compost_batches(batch_id)
);

CREATE TABLE compost_batches (
    batch_id SERIAL PRIMARY KEY,
    batch_date DATE NOT NULL,
    aeration_date DATE NOT NULL
);

CREATE TABLE turning_events (
    event_id SERIAL PRIMARY KEY,
    batch_id INT NOT NULL,
    turn_date DATE NOT NULL,
    FOREIGN KEY (batch_id) REFERENCES compost_batches(batch_id)
);

CREATE TABLE mixing_events (
    event_id SERIAL PRIMARY KEY,
    batch_id INT NOT NULL,
    mix_date DATE NOT NULL,
    FOREIGN KEY (batch_id) REFERENCES compost_batches(batch_id)
);

CREATE TABLE compost_batches (
    batch_id SERIAL PRIMARY KEY,
    batch_name VARCHAR(50) NOT NULL,
    turn_date DATE NOT NULL,
    mix_date DATE NOT NULL
);

CREATE TABLE compost_batches (
  batch_id SERIAL PRIMARY KEY,
  batch_date DATE,
  turn_date DATE,
  mix_date DATE
);

CREATE TABLE compost_locations (
  location_id SERIAL PRIMARY KEY,
  location_name VARCHAR(50)
);

CREATE TABLE batch_locations (
  batch_id INT REFERENCES compost_batches(batch_id),
  location_id INT REFERENCES compost_locations(location_id),
  PRIMARY KEY(batch_id, location_id)
);

CREATE TABLE batch_comments (
  comment_id SERIAL PRIMARY KEY,
  batch_id INT REFERENCES compost_batches(batch_id),
  comment TEXT
);

CREATE TABLE compost_materials (
  material_id SERIAL PRIMARY KEY,
  material_name VARCHAR(50),
  material_type VARCHAR(50)
);
