tables.table_schema:
CREATE TABLE compost_sources (
    id UUID PRIMARY KEY,
    source_name STRING,
    origin_location STRING,
    quality_rating INT
);

CREATE TABLE compost_categories (
    id UUID PRIMARY KEY,
    category_name STRING,
    description STRING
);

CREATE TABLE compost_logs (
    id UUID PRIMARY KEY,
    timestamp TIMESTAMP,
    source_id UUID REFERENCES compost_sources(id),
    category_id UUID REFERENCES compost_categories(id),
    weight DECIMAL
);

CREATE TABLE compost_sources (
  id INT PRIMARY KEY,
  source_name VARCHAR(50),
  location VARCHAR(100),
  contact_person VARCHAR(50)
);
