CREATE TABLE compost_pile (
  id INT PRIMARY KEY,
  location VARCHAR(255),
  date_created DATE,
  pH_level DECIMAL
);

CREATE TABLE compost_pile_ph_levels (
  id UUID PRIMARY KEY,
  pile_id UUID NOT NULL,
  ph_level FLOAT NOT NULL,
  recorded_at TIMESTAMP NOT NULL
);

CREATE TABLE compost_piles (
  id UUID PRIMARY KEY,
  location TEXT NOT NULL,
  temperature FLOAT NOT NULL,
  moisture_level FLOAT NOT NULL
);

CREATE TABLE compost_pile_logs (
  id UUID PRIMARY KEY,
  pile_id UUID NOT NULL,
  action TEXT NOT NULL,
  description TEXT
);

