tables.table_schema:
CREATE TABLE MoistureContentOfCompostHeaps (
  compost_heap_id UUID PRIMARY KEY,
  moisture_level DECIMAL,
  timestamp TIMESTAMP
);

CREATE TABLE compost_heaps ( 
    id UUID PRIMARY KEY, 
    heap_name VARCHAR(255) NOT NULL, 
    moisture_content DECIMAL NOT NULL, 
    last_updated TIMESTAMP NOT NULL 
);

CREATE TABLE moisture_logs ( 
    log_id UUID PRIMARY KEY, 
    heap_id UUID REFERENCES compost_heaps(id), 
    measured_at TIMESTAMP NOT NULL, 
    moisture_level DECIMAL NOT NULL 
);

CREATE TABLE compost_heaps (
  id INT PRIMARY KEY,
  heap_name VARCHAR(50) NOT NULL,
  moisture_level DECIMAL(5, 2) NOT NULL,
  last_updated TIMESTAMP
);
