tables.table_schema:
CREATE TABLE decomposition_rates (
  id SERIAL PRIMARY KEY,
  material_name VARCHAR(50) NOT NULL,
  decomposition_rate FLOAT NOT NULL
);

CREATE TABLE composting_process (
  process_id SERIAL PRIMARY KEY,
  material_id INT REFERENCES decomposition_rates(id),
  start_date DATE NOT NULL,
  end_date DATE
);

CREATE TABLE organic_materials(
  id SERIAL PRIMARY KEY,
  material_name TEXT,
  decomposition_rate FLOAT
);

CREATE TABLE compost_bins(
  id SERIAL PRIMARY KEY,
  bin_id TEXT,
  temperature FLOAT,
  moisture_level FLOAT
);

CREATE TABLE decomposition_logs(
  id SERIAL PRIMARY KEY,
  material_id INT,
  bin_id INT,
  decomposition_timestamp TIMESTAMP
);

CREATE TABLE Materials (
  material_id SERIAL PRIMARY KEY,
  material_name VARCHAR(100) NOT NULL,
  decomposition_rate DECIMAL(5,2)
);

CREATE TABLE CompostBins (
  bin_id SERIAL PRIMARY KEY,
  bin_location VARCHAR(100) NOT NULL,
  bin_capacity DECIMAL(5,2),
  material_id INT REFERENCES Materials(material_id)
);

CREATE TABLE TemperatureLogs (
  log_id SERIAL PRIMARY KEY,
  log_date DATE,
  temperature DECIMAL(5,2),
  bin_id INT REFERENCES CompostBins(bin_id)
);

CREATE TABLE MoistureLevels (
  level_id SERIAL PRIMARY KEY,
  level DECIMAL(5,2),
  log_id INT REFERENCES TemperatureLogs(log_id)
);

CREATE TABLE PHLevels (
  level_id SERIAL PRIMARY KEY,
  ph DECIMAL(5,2),
  log_id INT REFERENCES TemperatureLogs(log_id)
);
