tables.table_schema:
CREATE TABLE compost_maturity_levels (
    id INT PRIMARY KEY,
    maturity_level VARCHAR(50) NOT NULL,
    date_recorded DATE
);

CREATE TABLE testing_results (
    id INT PRIMARY KEY,
    test_name VARCHAR(50) NOT NULL,
    result DECIMAL(10, 2),
    sample_id INT,
    FOREIGN KEY (sample_id) REFERENCES compost_maturity_levels(id)
);

CREATE TABLE compost_maturity ( 
    id INT PRIMARY KEY, 
    maturity_level TEXT, 
    test_date DATE, 
    test_result TEXT 
);

CREATE TABLE reporting ( 
    id INT PRIMARY KEY, 
    report_date DATE, 
    report_content TEXT 
);

CREATE TABLE compost_maturity_levels (
  id INT PRIMARY KEY,
  maturity_level VARCHAR(50),
  date_recorded DATE
);

CREATE TABLE test_results (
  test_id INT PRIMARY KEY,
  maturity_level_id INT,
  test_name VARCHAR(50),
  test_result VARCHAR(255)
);

CREATE TABLE quality_control (
  record_id INT PRIMARY KEY,
  test_id INT,
  test_result VARCHAR(255),
  comments TEXT
);

CREATE TABLE reporting (
  report_id INT PRIMARY KEY,
  record_id INT,
  report_description TEXT,
  report_date DATE
);

CREATE TABLE compost_levels( 
  compost_id INT PRIMARY KEY, 
  maturity_level VARCHAR(50), 
  test_date DATE, 
  test_result VARCHAR(50) 
);

CREATE TABLE compost_test_types( 
  test_type_id INT PRIMARY KEY, 
  test_type_name VARCHAR(50), 
  description TEXT 
);

CREATE TABLE testers( 
  tester_id INT PRIMARY KEY, 
  tester_name VARCHAR(50), 
  department VARCHAR(50) 
);

CREATE TABLE compost_locations( 
  location_id INT PRIMARY KEY, 
  location_name VARCHAR(100), 
  location_address VARCHAR(200) 
);
