tables.table_schema:
CREATE TABLE hazardous_waste_disposal_routes(
  route_id INT PRIMARY KEY,
  route_description TEXT,
  disposal_method VARCHAR(50),
  disposal_location VARCHAR(100)
);

CREATE TABLE transportation_routes (
  route_id UUID PRIMARY KEY,
  route_name TEXT NOT NULL,
  route_description TEXT,
  route_method TEXT
);

CREATE TABLE waste_disposal_methods (
  method_id UUID PRIMARY KEY,
  method_name TEXT NOT NULL,
  method_description TEXT
);

CREATE TABLE HazardousWasteDisposalRoutes (
  route_id INT PRIMARY KEY,
  route_name VARCHAR(255) NOT NULL,
  disposal_method VARCHAR(50) NOT NULL,
  route_description TEXT
);

CREATE TABLE WasteDisposalVehicles (
  vehicle_id INT PRIMARY KEY,
  vehicle_type VARCHAR(50) NOT NULL,
  capacity_kg INT NOT NULL,
  restriction_notes TEXT
);

CREATE TABLE HazardousWasteTypes (
  waste_type_id INT PRIMARY KEY,
  waste_type_name VARCHAR(100) NOT NULL,
  disposal_guidelines TEXT
);

CREATE TABLE transport_methods (
  method_id INT PRIMARY KEY,
  method_name TEXT,
  method_description TEXT
);

CREATE TABLE disposal_sites (
  site_id INT PRIMARY KEY,
  site_name TEXT,
  site_location TEXT
);

CREATE TABLE waste_inventory (
  inventory_id INT PRIMARY KEY,
  inventory_name TEXT,
  quantity INT,
  site_id INT REFERENCES disposal_sites(site_id)
);
