tables.table_schema:
CREATE TABLE hazardous_waste_generation (
  id INT PRIMARY KEY,
  generation_date DATE,
  waste_type VARCHAR(50),
  generation_quantity DECIMAL
);

CREATE TABLE waste_disposal_facilities (
  facility_id INT PRIMARY KEY,
  facility_name VARCHAR(100),
  location VARCHAR(200),
  disposal_capacity DECIMAL
);

CREATE TABLE waste_generation_sites (
  site_id INT PRIMARY KEY,
  site_name VARCHAR(100),
  location VARCHAR(200),
  generated_waste_type VARCHAR(50)
);

CREATE TABLE hazardous_waste_generation (
  id INT PRIMARY KEY,
  waste_type VARCHAR(255),
  generation_date DATE,
  quantity_kg DECIMAL
);

CREATE TABLE waste_disposal_sites (
  site_id INT PRIMARY KEY,
  site_name VARCHAR(255),
  location VARCHAR(255),
  capacity_kg DECIMAL
);

CREATE TABLE waste_disposal_records (
  record_id INT PRIMARY KEY,
  waste_id INT,
  disposal_site_id INT,
  disposal_date DATE
);

CREATE TABLE waste_analysis_report (
  report_id INT PRIMARY KEY,
  waste_id INT,
  analysis_date DATE,
  analysis_result VARCHAR(255)
);

CREATE TABLE waste_impact_assessment (
  assessment_id INT PRIMARY KEY,
  waste_id INT,
  assessment_date DATE,
  impact_level VARCHAR(255)
);
