tables.table_schema:
CREATE TABLE hazardous_waste_disposal_costs (
  disposal_method_id INT PRIMARY KEY,
  method_name VARCHAR(255),
  cost DECIMAL(10, 2)
);

CREATE TABLE hazardous_waste_disposal_effectiveness (
  disposal_method_id INT PRIMARY KEY,
  method_name VARCHAR(255),
  effectiveness_percent DECIMAL(5, 2)
);

CREATE TABLE hazardous_waste_disposal_methods (
  disposal_method_id INT PRIMARY KEY,
  method_name VARCHAR(50),
  cost DECIMAL(10,2),
  effectiveness DECIMAL(5,2)
);

CREATE TABLE waste_sites (
  site_id INT PRIMARY KEY,
  site_name VARCHAR(50),
  location VARCHAR(100),
  capacity INT
);

CREATE TABLE HazardousWasteDisposalMethods (
  method_id INT PRIMARY KEY,
  method_name VARCHAR(50) NOT NULL,
  cost DECIMAL(10, 2) NOT NULL,
  effectiveness DECIMAL(5, 2) NOT NULL
);

CREATE TABLE WasteDisposalCompanies (
  company_id INT PRIMARY KEY,
  company_name VARCHAR(100) NOT NULL,
  contact_person VARCHAR(50),
  email VARCHAR(50) UNIQUE
);
