tables.table_schema:
CREATE TABLE hazardous_waste_pickup (
    id INT PRIMARY KEY,
    pickup_date DATE,
    pickup_location VARCHAR(255),
    pickup_status VARCHAR(50)
);

CREATE TABLE waste_disposal (
    id INT PRIMARY KEY,
    disposal_date DATE,
    disposal_location VARCHAR(255),
    disposal_method VARCHAR(100)
);

CREATE TABLE waste_disposal_facilities (
    facility_id INT PRIMARY KEY,
    facility_name VARCHAR(100),
    location VARCHAR(100),
    capacity_in_tons INT
);

CREATE TABLE waste_types (
    id INT PRIMARY KEY,
    name VARCHAR(50),
    description TEXT
);

CREATE TABLE disposal_sites (
    id INT PRIMARY KEY,
    name VARCHAR(100),
    address VARCHAR(255),
    capacity DECIMAL
);

CREATE TABLE customers (
	customer_id INT PRIMARY KEY,
	customer_name VARCHAR(100),
	contact_email VARCHAR(100),
	phone_number VARCHAR(20)
);

CREATE TABLE waste_disposal_sites (
	site_id INT PRIMARY KEY,
	site_name VARCHAR(100),
	location VARCHAR(100),
	capacity INT
);

CREATE TABLE service_providers (
	provider_id INT PRIMARY KEY,
	provider_name VARCHAR(100),
	specialization VARCHAR(100),
	contact_email VARCHAR(100)
);
