tables.table_schema:
CREATE TABLE trained_personnel (
    id INT PRIMARY KEY,
    name VARCHAR(50),
    department VARCHAR(50),
    training_date DATE
);

CREATE TABLE Personnel (
    id SERIAL PRIMARY KEY,
    name VARCHAR(50) NOT NULL,
    job_title VARCHAR(50) NOT NULL,
    training_completed BOOLEAN
);

CREATE TABLE Contractors (
    id SERIAL PRIMARY KEY,
    name VARCHAR(50) NOT NULL,
    company VARCHAR(50) NOT NULL,
    license_number VARCHAR(20) NOT NULL
);

CREATE TABLE HazardousWasteHandling (
    id SERIAL PRIMARY KEY,
    personnel_id INT NOT NULL,
    contractor_id INT NOT NULL,
    date_handled TIMESTAMP
);

CREATE TABLE Personnel (
    personnel_id INT PRIMARY KEY,
    name VARCHAR(50),
    department VARCHAR(50),
    training_completed BOOLEAN
);

CREATE TABLE Contractors (
    contractor_id INT PRIMARY KEY,
    name VARCHAR(50),
    company_name VARCHAR(50),
    certification_status BOOLEAN
);

CREATE TABLE Hazardous_Waste (
    waste_id INT PRIMARY KEY,
    waste_type VARCHAR(50),
    quantity DECIMAL,
    disposal_method VARCHAR(50)
);

CREATE TABLE Personnel_Contractor_Relationship (
    relationship_id INT PRIMARY KEY,
    personnel_id INT,
    contractor_id INT,
    relationship_type VARCHAR(50)
);
