tables.table_schema:
CREATE TABLE Hazardous_Waste_Disposal_Sites (
  site_id INT PRIMARY KEY,
  site_name VARCHAR(50),
  location GEOGRAPHY,
  disposal_type VARCHAR(20)
);

CREATE TABLE Community_Proximity (
  community_id INT PRIMARY KEY,
  community_name VARCHAR(50),
  location GEOGRAPHY,
  proximity_to_site DOUBLE PRECISION
);

CREATE TABLE hazardous_waste_disposal_sites (
  site_id INT PRIMARY KEY,
  site_name VARCHAR(255),
  location GEOGRAPHY,
  disposal_capacity INT
);

CREATE TABLE communities (
  community_id INT PRIMARY KEY,
  community_name VARCHAR(255),
  location GEOGRAPHY
);

CREATE TABLE proximity_data (
  proximity_id INT PRIMARY KEY,
  site_id INT,
  community_id INT,
  distance FLOAT
);

CREATE TABLE WasteDisposalSites (
	site_id INT PRIMARY KEY,
	site_name VARCHAR(255),
	location_point GEOMETRY(Point),
	is_hazardous BOOLEAN
);

CREATE TABLE Communities (
	community_id INT PRIMARY KEY,
	community_name VARCHAR(255),
	community_location GEOMETRY(Point),
	population INT
);

CREATE TABLE ProximityData (
	proximity_id INT PRIMARY KEY,
	site_id INT,
	community_id INT,
	distance FLOAT,
	FOREIGN KEY (site_id) REFERENCES WasteDisposalSites(site_id),
	FOREIGN KEY (community_id) REFERENCES Communities(community_id)
);

CREATE TABLE HazardTypes (
	hazard_id INT PRIMARY KEY,
	hazard_name VARCHAR(255),
	risk_level VARCHAR(50)
);
