tables.table_schema:
CREATE TABLE events (
  event_id INT PRIMARY KEY,
  event_name VARCHAR(255),
  event_date DATE,
  event_location VARCHAR(255)
);

CREATE TABLE collection_events (
	event_id INT PRIMARY KEY,
	event_date DATE,
	event_location VARCHAR(255),
	event_type VARCHAR(100)
);

CREATE TABLE collection_locations (
	location_id INT PRIMARY KEY,
	location_name VARCHAR(255),
	location_address VARCHAR(255),
	location_contact_person VARCHAR(255)
);

CREATE TABLE waste_items (
	item_id INT PRIMARY KEY,
	item_name VARCHAR(100),
	item_category VARCHAR(50),
	item_weight DECIMAL(10,2)
);

CREATE TABLE event_participants (
    participant_id UUID PRIMARY KEY,
    participant_name VARCHAR(100),
    event_id UUID REFERENCES collection_events(event_id),
    participation_status VARCHAR(50)
);

CREATE TABLE collection_locations (
    location_id UUID PRIMARY KEY,
    location_name VARCHAR(100),
    location_address TEXT,
    location_capacity INT
);

CREATE TABLE collected_items (
    item_id UUID PRIMARY KEY,
    item_name VARCHAR(100),
    item_weight DECIMAL(10,2),
    event_id UUID REFERENCES collection_events(event_id)
);

CREATE TABLE disposal_records (
    record_id UUID PRIMARY KEY,
    disposal_date DATE,
    item_id UUID REFERENCES collected_items(item_id),
    location_id UUID REFERENCES collection_locations(location_id)
);
