tables.table_schema:
CREATE TABLE waste_analysis (
	id SERIAL PRIMARY KEY,
	waste_type VARCHAR(50) NOT NULL,
	average_weight DECIMAL NOT NULL,
	analysis_date DATE NOT NULL
);

CREATE TABLE electronic_waste (
    id INT PRIMARY KEY,
    waste_type VARCHAR(50) NOT NULL,
    weight DECIMAL(10,2) NOT NULL,
    collection_date DATE NOT NULL
);

CREATE TABLE waste_recycling_facilities (
    facility_id INT PRIMARY KEY,
    facility_name VARCHAR(100) NOT NULL,
    location VARCHAR(255) NOT NULL,
    capacity DECIMAL(10,2) NOT NULL
);

CREATE TABLE waste_collection_points (
    point_id INT PRIMARY KEY,
    point_name VARCHAR(100) NOT NULL,
    location VARCHAR(255) NOT NULL,
    collection_capacity DECIMAL(10,2) NOT NULL
);

CREATE TABLE collected_waste_log (
    log_id INT PRIMARY KEY,
    collection_point_id INT NOT NULL,
    weight_collected DECIMAL(10,2) NOT NULL,
    collection_date DATE NOT NULL
);

CREATE TABLE recycling_report (
    report_id INT PRIMARY KEY,
    facility_id INT NOT NULL,
    collected_weight DECIMAL(10,2) NOT NULL,
    report_date DATE NOT NULL
);
