tables.table_schema:
CREATE TABLE WasteRegulations (
  id INT PRIMARY KEY,
  regulation_name VARCHAR(255) NOT NULL,
  disposal_method VARCHAR(255) NOT NULL,
  enforcement_agency VARCHAR(255) NOT NULL
);

CREATE TABLE electronic_waste_disposal_regulations ( 
    regulation_id INT PRIMARY KEY, 
    regulation_name VARCHAR(255) NOT NULL, 
    disposal_method VARCHAR(255), 
    enforcement_agency VARCHAR(255) 
);

CREATE TABLE compliance_requirements ( 
    requirement_id INT PRIMARY KEY, 
    requirement_description TEXT, 
    regulation_id INT, 
    FOREIGN KEY (regulation_id) REFERENCES electronic_waste_disposal_regulations(regulation_id) 
);

CREATE TABLE disposal_sites ( 
    site_id INT PRIMARY KEY, 
    site_name VARCHAR(255) NOT NULL, 
    location VARCHAR(255), 
    contact_person VARCHAR(255) 
);

CREATE TABLE disposal_regulations (
    id UUID PRIMARY KEY,
    regulation_name STRING,
    applicable_region STRING,
    regulation_details TEXT
);

CREATE TABLE compliance_requirements (
    id UUID PRIMARY KEY,
    requirement_name STRING,
    requirement_description TEXT,
    applicable_regulation_id UUID FOREIGN KEY REFERENCES disposal_regulations(id)
);

CREATE TABLE disposal_facilities (
    id UUID PRIMARY KEY,
    facility_name STRING,
    location TEXT,
    capacity INT
);

CREATE TABLE disposal_records (
    id UUID PRIMARY KEY,
    disposal_date DATE,
    disposed_item STRING,
    disposal_facility_id UUID FOREIGN KEY REFERENCES disposal_facilities(id)
);

CREATE TABLE regulations (
  regulation_id INT PRIMARY KEY,
  regulation_name VARCHAR(255),
  regulation_description TEXT
);

CREATE TABLE compliance_categories (
  category_id INT PRIMARY KEY,
  category_name VARCHAR(255),
  category_description TEXT
);

CREATE TABLE companies (
  company_id INT PRIMARY KEY,
  company_name VARCHAR(255),
  company_address VARCHAR(255),
  company_contact_person VARCHAR(255)
);

CREATE TABLE waste_disposal_sites (
  site_id INT PRIMARY KEY,
  site_name VARCHAR(255),
  site_location VARCHAR(255),
  site_capacity DOUBLE
);
