tables.table_schema:
CREATE TABLE e_waste_recycling_centers (
  center_id INT PRIMARY KEY,
  center_name VARCHAR(50) NOT NULL,
  location VARCHAR(100) NOT NULL,
  recycling_capacity INT
);

CREATE TABLE e_waste_recycling_rates (
  id INT PRIMARY KEY,
  city VARCHAR(255),
  recycling_rate DECIMAL(5,2),
  impact_score INT
);

CREATE TABLE recycling_impact_evaluation (
  evaluation_id INT PRIMARY KEY,
  initiative_id INT,
  evaluation_date DATE,
  impact_description TEXT
);

CREATE TABLE ewaste_locations (
  location_id INT PRIMARY KEY,
  city VARCHAR(50),
  state VARCHAR(50),
  country VARCHAR(50)
);

CREATE TABLE ewaste_recycling_facilities (
  facility_id INT PRIMARY KEY,
  facility_name VARCHAR(100),
  location_id INT,
  capacity INT
);

CREATE TABLE ewaste_collection_records (
  record_id INT PRIMARY KEY,
  facility_id INT,
  collection_date DATE,
  collection_weight DECIMAL(10,2)
);

CREATE TABLE ewaste_recycling_rates (
  rate_id INT PRIMARY KEY,
  facility_id INT,
  process_date DATE,
  recycling_rate DECIMAL(5,2)
);
