tables.table_schema:
CREATE TABLE refurbishment_records (
	refurbishment_id INT PRIMARY KEY,
	electronic_component TEXT NOT NULL,
	refurbishment_date DATE NOT NULL,
	refurbishment_cost DECIMAL(10, 2) NOT NULL
);

CREATE TABLE resale_records (
	resale_id INT PRIMARY KEY,
	electronic_component TEXT NOT NULL,
	resale_date DATE NOT NULL,
	resale_price DECIMAL(10, 2) NOT NULL
);

CREATE TABLE refurbishment (
  component_id INT PRIMARY KEY,
  component_name VARCHAR(50) NOT NULL,
  refurbishment_date DATE,
  refurbishment_notes TEXT
);

CREATE TABLE refurbishment_components (
    component_id INT PRIMARY KEY,
    component_name VARCHAR(255),
    refurbishment_date DATE,
    refurbishment_cost DECIMAL(10, 2)
);

CREATE TABLE resale_components (
    component_id INT PRIMARY KEY,
    component_name VARCHAR(255),
    resale_date DATE,
    resale_price DECIMAL(10, 2)
);

CREATE TABLE component_stock (
    component_id INT PRIMARY KEY,
    quantity INT,
    location VARCHAR(100),
    in_stock BOOLEAN
);

CREATE TABLE component_logs (
    log_id INT PRIMARY KEY,
    component_id INT,
    action VARCHAR(50),
    log_date DATETIME
);

CREATE TABLE refurbishment (
    component_id INT PRIMARY KEY,
    component_name VARCHAR(255) NOT NULL,
    refurbishment_date DATE,
    refurbishment_cost DECIMAL(10, 2)
);

CREATE TABLE component_inventory (
    inventory_id INT PRIMARY KEY,
    component_id INT,
    quantity INT,
    location VARCHAR(255)
);

CREATE TABLE component_supplier (
    supplier_id INT PRIMARY KEY,
    supplier_name VARCHAR(255),
    contact_person VARCHAR(255),
    contact_number VARCHAR(15)
);

CREATE TABLE component_tracking (
    tracking_id INT PRIMARY KEY,
    component_id INT,
    action_date DATE,
    action_description TEXT
);
