tables.table_schema:
CREATE TABLE ewaste_recycling_activities (
  activity_id INT PRIMARY KEY,
  energy_savings FLOAT,
  emissions_reduction FLOAT,
  resource_conservation FLOAT
);

CREATE TABLE recycling_facilities (
  facility_id INT PRIMARY KEY,
  facility_name VARCHAR(255),
  location VARCHAR(255),
  capacity INT
);

CREATE TABLE energy_savings (
   id INT PRIMARY KEY,
   amount FLOAT,
   date DATE
);

CREATE TABLE emissions_reduction (
   id INT PRIMARY KEY,
   location VARCHAR(255),
   reduction_amount FLOAT,
   date DATE
);

CREATE TABLE resource_conservation (
   id INT PRIMARY KEY,
   resource_type VARCHAR(255),
   amount_saved FLOAT,
   date DATE
);

CREATE TABLE energy_savings (
  id INT PRIMARY KEY,
  recycling_activity_id INT,
  energy_saved FLOAT,
  date_saved TIMESTAMP
);

CREATE TABLE emissions_reduction (
  id INT PRIMARY KEY,
  recycling_activity_id INT,
  emission_reduction FLOAT,
  date_reduced TIMESTAMP
);

CREATE TABLE resource_conservation (
  id INT PRIMARY KEY,
  recycling_activity_id INT,
  resource_name VARCHAR(50),
  amount_saved INT
);

CREATE TABLE recycling_activities (
  id INT PRIMARY KEY,
  activity_name VARCHAR(50),
  location VARCHAR(100),
  date_started TIMESTAMP
);
