tables.table_schema:
CREATE TABLE WasteToEnergyPlantDailyWaste (
    plant_id INT,
    processing_date DATE,
    waste_type VARCHAR(50),
    amount_processed FLOAT
);

CREATE TABLE WasteToEnergyPlant (
  plant_id INT,
  plant_name VARCHAR(255),
  location VARCHAR(255),
  PRIMARY KEY (plant_id)
);

CREATE TABLE waste_management (
  date DATE,
  amount_processed FLOAT
);

CREATE TABLE waste_categories (
  category_id SERIAL PRIMARY KEY,
  category_name VARCHAR(50) UNIQUE
);

CREATE TABLE plant_information (
  plant_id SERIAL PRIMARY KEY,
  plant_name VARCHAR(50) UNIQUE,
  location VARCHAR(100)
);

CREATE TABLE WasteToEnergyPlant (
    plant_id INT PRIMARY KEY,
    plant_name VARCHAR(50),
    location VARCHAR(100)
);

CREATE TABLE WasteProcessed (
    ws_id INT PRIMARY KEY,
    plant_id INT,
    date_processed DATE,
    waste_amount DECIMAL(10,2)
);

CREATE TABLE WasteCategories (
    category_id INT PRIMARY KEY,
    category_name VARCHAR(50)
);

CREATE TABLE DailyProcessed (
    dp_id INT PRIMARY KEY,
    ws_id INT,
    category_id INT,
    processing_time TIME
);

CREATE TABLE PlantEmployees (
    emp_id INT PRIMARY KEY,
    plant_id INT,
    emp_name VARCHAR(50),
    department VARCHAR(50)
);
