tables.table_schema:
CREATE TABLE efficiency_data (
    equipment_id UUID,
    efficiency_percentage DECIMAL,
    date_recorded DATE
);

CREATE TABLE gasification_equipment_performance (
  equipment_id INT,
  date TIMESTAMP,
  efficiency FLOAT
);

CREATE TABLE equipment_details (
  equipment_id INT,
  manufacturer VARCHAR(255),
  model VARCHAR(255),
  production_year INT
);

CREATE TABLE gasification_equipment (
    id INT PRIMARY KEY,
    equipment_name TEXT,
    manufacturer TEXT,
    installation_date DATE
);

CREATE TABLE equipment_status (
    id INT PRIMARY KEY,
    equipment_id INT,
    status TEXT,
    last_update DATETIME
);
