tables.table_schema:
CREATE TABLE emission_levels (
    timestamp TIMESTAMP,
    facility_id INT,
    emission_type VARCHAR(50),
    emission_level DECIMAL
);

CREATE TABLE EmissionLevels (
    id INT PRIMARY KEY,
    facility_id INT,
    emission_date DATE,
    emission_level DECIMAL
);

CREATE TABLE WasteToEnergyFacilities (
    facility_id INT PRIMARY KEY,
    facility_name VARCHAR(255),
    location VARCHAR(255),
    capacity DECIMAL
);

CREATE TABLE EnvironmentalImpactAssessment (
    assessment_id INT PRIMARY KEY,
    facility_id INT,
    assessment_date DATE,
    environmental_impact VARCHAR(255)
);

CREATE TABLE EmissionData (
   id UUID PRIMARY KEY,
   date DATE,
   emission_level DECIMAL,
   facility_name VARCHAR(255)
);

CREATE TABLE Facility (
   id UUID PRIMARY KEY,
   name VARCHAR(255),
   location VARCHAR(255),
   capacity DECIMAL
);

CREATE TABLE Operation (
   id UUID PRIMARY KEY,
   facility_id UUID,
   start_date DATE,
   end_date DATE
);

CREATE TABLE EmissionLevels (
  id INT PRIMARY KEY,
  date DATE NOT NULL,
  operation_id INT NOT NULL,
  emission_level DECIMAL NOT NULL
);

CREATE TABLE Operations (
  id INT PRIMARY KEY,
  operation_name VARCHAR(255) NOT NULL
);

CREATE TABLE Plants (
  id INT PRIMARY KEY,
  plant_name VARCHAR(255) NOT NULL,
  location VARCHAR(255) NOT NULL
);

CREATE TABLE EmissionSources (
  id INT PRIMARY KEY,
  source_name VARCHAR(255) NOT NULL
);

CREATE TABLE PlantEmissions (
  plant_id INT NOT NULL,
  source_id INT NOT NULL,
  emission_value DECIMAL NOT NULL
);
