tables.table_schema:
CREATE TABLE heat_exchanger (
    id INT PRIMARY KEY,
    inlet_temperature FLOAT,
    outlet_temperature FLOAT,
    efficiency FLOAT
);

CREATE TABLE heat_reclaimer (
    id INT PRIMARY KEY,
    inlet_flow_rate FLOAT,
    outlet_flow_rate FLOAT,
    heat_transfer_rate FLOAT
);

CREATE TABLE efficiency_analysis (
    id INT PRIMARY KEY,
    timestamp TIMESTAMP,
    efficiency FLOAT
);

CREATE TABLE efficiency_report (
    id INT PRIMARY KEY,
    report_date DATE,
    efficiency FLOAT
);

CREATE TABLE HeatRecoveryProcess ( 
  id INT PRIMARY KEY, 
  temperature_in DECIMAL, 
  temperature_out DECIMAL, 
  efficiency DECIMAL 
);

CREATE TABLE HeatExchangerData ( 
  id INT PRIMARY KEY, 
  exchange_type VARCHAR(50), 
  surface_area DECIMAL, 
  efficiency DECIMAL 
);

CREATE TABLE EnergyConsumptionData ( 
  data_id INT PRIMARY KEY, 
  device_id INT, 
  power_consumed DECIMAL, 
  timestamp TIMESTAMP 
);

CREATE TABLE EfficiencyAnalysis ( 
  analysis_id INT PRIMARY KEY, 
  analysis_type VARCHAR(50), 
  efficiency_score DECIMAL, 
  timestamp TIMESTAMP 
);
