tables.table_schema:
CREATE TABLE marine_locations (
    location_id INT PRIMARY KEY,
    location_name VARCHAR(255),
    latitude FLOAT,
    longitude FLOAT
);

CREATE TABLE marine_locations (
    location_id INT PRIMARY KEY,
    location_name VARCHAR(100) UNIQUE,
    country VARCHAR(50),
    region VARCHAR(50)
);

CREATE TABLE Location (
    location_id INT PRIMARY KEY,
    location_name VARCHAR(50) NOT NULL
);

CREATE TABLE MarineOrganisms (
    organism_id INT PRIMARY KEY,
    organism_name VARCHAR(50)
);
