tables.table_schema:
CREATE TABLE microplastics_studies (
  study_id INT PRIMARY KEY,
  study_name VARCHAR(255) NOT NULL,
  location VARCHAR(255),
  study_date DATE
);

CREATE TABLE research_team (
  id INT PRIMARY KEY,
  team_name VARCHAR(50),
  lead_scientist VARCHAR(50),
  research_area VARCHAR(50)
);

CREATE TABLE measurement_units (
  id INT PRIMARY KEY,
  unit_name VARCHAR(50),
  quantity_type VARCHAR(50)
);

CREATE TABLE accumulation_data (
  sample_id INT PRIMARY KEY,
  timestamp TIMESTAMP,
  location VARCHAR,
  microplastic_type VARCHAR
);

CREATE TABLE analysis_results (
  analysis_id INT PRIMARY KEY,
  sample_id INT,
  microplastic_count INT,
  analysis_method VARCHAR
);

CREATE TABLE locations (
  location_id INT PRIMARY KEY,
  location_name VARCHAR,
  country VARCHAR
);

CREATE TABLE researchers (
  researcher_id INT PRIMARY KEY,
  researcher_name VARCHAR,
  affiliation VARCHAR
);

CREATE TABLE research_projects (
  project_id INT PRIMARY KEY,
  project_name VARCHAR,
  start_date DATE,
  end_date DATE
);
