tables.table_schema:
CREATE TABLE air_quality (
    port_name VARCHAR PRIMARY KEY,
    air_quality_index INT,
    pollution_level VARCHAR,
    last_updated DATE
);

CREATE TABLE emissions (
    ship_id INT,
    emission_category VARCHAR(255),
    emission_type VARCHAR(255),
    emission_amount DECIMAL
);

CREATE TABLE air_quality (
    area_id INT,
    area_name VARCHAR(255),
    pollutant_name VARCHAR(255),
    pollutant_level DECIMAL
);

CREATE TABLE impact (
    ship_id INT,
    area_id INT,
    impact_level DECIMAL
);

CREATE TABLE emissions_data ( 
  emission_id INT PRIMARY KEY, 
  ship_id INT, 
  emission_type VARCHAR(255), 
  emission_amount FLOAT 
);

CREATE TABLE ships ( 
  ship_id INT PRIMARY KEY, 
  ship_name VARCHAR(255), 
  ship_type VARCHAR(255), 
  ship_country VARCHAR(255) 
);

CREATE TABLE locations ( 
  location_id INT PRIMARY KEY, 
  location_name VARCHAR(255), 
  location_latitude FLOAT, 
  location_longitude FLOAT 
);

CREATE TABLE air_quality ( 
  air_quality_id INT PRIMARY KEY, 
  location_id INT, 
  air_quality_index INT, 
  air_pollution_level VARCHAR(255) 
);

CREATE TABLE emissions_impact ( 
  impact_id INT PRIMARY KEY, 
  emission_id INT, 
  air_quality_id INT, 
  impact_description TEXT 
);
