tables.table_schema:
CREATE TABLE water_samples (
    sample_id INT PRIMARY KEY,
    source_name VARCHAR(50),
    pollution_level FLOAT
);

CREATE TABLE WaterSamples (
    sample_id INT PRIMARY KEY,
    source_id INT,
    chemical_name VARCHAR(50),
    concentration DECIMAL
);

CREATE TABLE PollutionSources (
    source_id INT PRIMARY KEY,
    source_name VARCHAR(50),
    location VARCHAR(100),
    pollution_level DECIMAL
);

CREATE TABLE pollution_sources (
    source_id INT PRIMARY KEY,
    source_name VARCHAR(255),
    location VARCHAR(255),
    type ENUM('industrial', 'agricultural', 'sewage')
);

CREATE TABLE PollutionTypes (
    type_id INT,
    type_name VARCHAR(50),
    description TEXT
);
