);

CREATE TABLE packaging_materials (
    material_id INT PRIMARY KEY,
    material_name VARCHAR(50) NOT NULL,
    sustainability_score FLOAT NOT NULL
);

CREATE TABLE material_properties (
    property_id INT PRIMARY KEY,
    property_name VARCHAR(50) NOT NULL,
    property_description TEXT
);

CREATE TABLE material_metrics (
    metric_id INT PRIMARY KEY,
    metric_name VARCHAR(50) NOT NULL,
    metric_value FLOAT NOT NULL
);

CREATE TABLE packaging_materials (
   id SERIAL PRIMARY KEY,
   material_name VARCHAR(50) NOT NULL,
   sustainability_rating INTEGER NOT NULL
);

CREATE TABLE material_categories (
   category_id SERIAL PRIMARY KEY,
   category_name VARCHAR(50) NOT NULL
);

CREATE TABLE material_metrics (
   metric_id SERIAL PRIMARY KEY,
   metric_name VARCHAR(50) NOT NULL,
   unit_of_measure VARCHAR(20) NOT NULL
);

CREATE TABLE packaging_metrics (
   packaging_id SERIAL PRIMARY KEY,
   packaging_material_id INTEGER REFERENCES packaging_materials(id),
   metric_id INTEGER REFERENCES material_metrics(metric_id),
   value FLOAT
);

CREATE TABLE PackagingMaterial (
  material_id INT PRIMARY KEY,
  material_name VARCHAR(50) NOT NULL,
  category VARCHAR(50) NOT NULL,
  recyclable BOOLEAN NOT NULL
);

CREATE TABLE Supplier (
  supplier_id INT PRIMARY KEY,
  supplier_name VARCHAR(50) NOT NULL,
  location VARCHAR(50) NOT NULL
);

CREATE TABLE PackagingMetrics (
  packaging_id INT PRIMARY KEY,
  material_id INT NOT NULL,
  quantity INT NOT NULL,
  CONSTRAINT fk_material FOREIGN KEY (material_id) REFERENCES PackagingMaterial(material_id)
);
