tables.table_schema:
CREATE TABLE carbon_footprint (
    production_process_id INT PRIMARY KEY,
    packaging_material VARCHAR(255),
    carbon_emission FLOAT
);

CREATE TABLE packaging_materials (
    material_id UUID PRIMARY KEY,
    material_name STRING,
    carbon_footprint FLOAT
);

CREATE TABLE packaging_processes (
    process_id UUID PRIMARY KEY,
    process_name STRING,
    process_description STRING,
    material_id UUID REFERENCES packaging_materials(material_id)
);

CREATE TABLE PackagingMaterials (
   material_id UUID PRIMARY KEY,
   material_name STRING,
   carbon_footprint FLOAT
);

CREATE TABLE ProductionProcesses (
   process_id UUID PRIMARY KEY,
   process_name STRING,
   process_description TEXT,
   carbon_emission_rate FLOAT
);

CREATE TABLE PackagingProcesses (
   packaging_id UUID PRIMARY KEY,
   packaging_name STRING,
   material_id UUID REFERENCES PackagingMaterials(material_id),
   process_id UUID REFERENCES ProductionProcesses(process_id)
);

CREATE TABLE Packaging_Companies (
  company_id INT PRIMARY KEY,
  company_name VARCHAR(50) NOT NULL,
  location VARCHAR(100) NOT NULL
);

CREATE TABLE Packaging_Materials (
  material_id INT PRIMARY KEY,
  material_type VARCHAR(50) NOT NULL,
  material_source VARCHAR(100) NOT NULL,
  emissions_factor FLOAT NOT NULL
);

CREATE TABLE Packaging_Users (
  user_id INT PRIMARY KEY,
  username VARCHAR(50) NOT NULL,
  email VARCHAR(100) NOT NULL
);
