tables.table_schema:
CREATE TABLE packaging_methods (
  method_id SERIAL PRIMARY KEY,
  method_name VARCHAR(50) NOT NULL,
  carbon_footprint DECIMAL
);

CREATE TABLE transportation_modes (
  mode_id SERIAL PRIMARY KEY,
  mode_name VARCHAR(50) NOT NULL,
  emissions_factor DECIMAL
);

CREATE TABLE packaging_methods (
    method_id INT PRIMARY KEY,
    method_name VARCHAR(50),
    carbon_footprint DECIMAL
);

CREATE TABLE transportation_modes (
    mode_id INT PRIMARY KEY,
    mode_name VARCHAR(50),
    emissions_factor DECIMAL
);

CREATE TABLE packaging_method (
  package_id INT PRIMARY KEY,
  method_name VARCHAR(255),
  emissions_factor DECIMAL(10,2)
);

CREATE TABLE transportation_method (
  method_id INT PRIMARY KEY,
  method_name VARCHAR(255),
  distance_covered DECIMAL(10,2),
  emissions_factor DECIMAL(10,2)
);

CREATE TABLE emissions_report (
  report_id INT PRIMARY KEY,
  report_date DATE,
  total_emissions DECIMAL(10,2),
  CONSTRAINT positive_emissions CHECK(total_emissions > 0)
);

CREATE TABLE packaging_materials (
    material_id INT PRIMARY KEY,
    material_name VARCHAR(50) NOT NULL,
    material_type VARCHAR(50) NOT NULL
);

CREATE TABLE transportation_methods (
    method_id INT PRIMARY KEY,
    method_name VARCHAR(50) NOT NULL,
    carbon_emission DECIMAL(10,2) NOT NULL
);

CREATE TABLE packaging_cost (
    cost_id INT PRIMARY KEY,
    material_id INT NOT NULL,
    method_id INT NOT NULL,
    cost DECIMAL(10,2) NOT NULL
);

CREATE TABLE carbon_emission_report (
    report_id INT PRIMARY KEY,
    report_date DATE NOT NULL,
    shipment_id INT NOT NULL,
    carbon_emission DECIMAL(10,2) NOT NULL
);
