tables.table_schema:
CREATE TABLE packaging_materials (
  id INT PRIMARY KEY,
  material_name VARCHAR(50),
  biodegradable BOOLEAN
);

CREATE TABLE biodegradable_materials_usage (
    id INT PRIMARY KEY,
    material_name VARCHAR(255),
    quantity_used DECIMAL(10, 2),
    usage_date DATE
);

CREATE TABLE material_composition (
    id INT PRIMARY KEY,
    material_name VARCHAR(255),
    compostable BOOLEAN,
    recyclable BOOLEAN
);

CREATE TABLE material_producers (
    id INT PRIMARY KEY,
    producer_name VARCHAR(255),
    location VARCHAR(255)
);
