tables.table_schema:
CREATE TABLE packaging_materials (
  material_id INT PRIMARY KEY,
  material_name VARCHAR(50) NOT NULL,
  recycling_rate FLOAT
);

CREATE TABLE recycling_bins (
  bin_id INT PRIMARY KEY,
  bin_location VARCHAR(100),
  material_id INT,
  FOREIGN KEY (material_id) REFERENCES packaging_materials(material_id)
);

CREATE TABLE PackagingTypes (
    type_id INT PRIMARY KEY,
    type_name VARCHAR(50),
    category VARCHAR(50),
    recycling_rate DECIMAL
);

CREATE TABLE CollectionSites (
    site_id INT PRIMARY KEY,
    site_name VARCHAR(50),
    location VARCHAR(100),
    contact_person VARCHAR(50)
);

CREATE TABLE PackagingTypes (
  id INT PRIMARY KEY,
  type VARCHAR(50),
  description TEXT
);

CREATE TABLE RecyclingCompanies (
  id INT PRIMARY KEY,
  name VARCHAR(50),
  location VARCHAR(100),
  contact_person VARCHAR(50)
);

CREATE TABLE packaging_materials (
  material_id INT PRIMARY KEY,
  material_name VARCHAR(50),
  recycling_category VARCHAR(50),
  recycling_rate DECIMAL(5,2)
);

CREATE TABLE collection_schedule (
  schedule_id INT PRIMARY KEY,
  center_id INT,
  collection_day VARCHAR(20),
  collection_time TIME
);
