tables.table_schema:
CREATE TABLE packaging_energy_consumption (
    plant_id INT PRIMARY KEY,
    plant_name VARCHAR(255),
    energy_consumption_kwh DECIMAL(10,2),
    production_date DATE
);

CREATE TABLE packaging_plants (
  plant_id INT PRIMARY KEY,
  plant_name VARCHAR(100),
  city VARCHAR(100),
  country VARCHAR(100)
);

CREATE TABLE packaging_plants (
    plant_id INT PRIMARY KEY,
    plant_name VARCHAR(50),
    location VARCHAR(100),
    total_energy_consumption DECIMAL
);

CREATE TABLE packaging_types (
    type_id INT PRIMARY KEY,
    type_name VARCHAR(50),
    material_type VARCHAR(50),
    recyclable BOOLEAN
);

CREATE TABLE Packaging_Plants (
  plant_id INT PRIMARY KEY,
  plant_name VARCHAR(100),
  location VARCHAR(255),
  energy_consumption DECIMAL
);

CREATE TABLE Products (
  product_id INT PRIMARY KEY,
  product_name VARCHAR(100),
  line_id INT,
  FOREIGN KEY (line_id) REFERENCES Production_Lines(line_id)
);

CREATE TABLE packaging_plants (
    plant_id INT PRIMARY KEY,
    plant_name VARCHAR(255),
    location VARCHAR(255),
    total_energy_consumed DECIMAL
);

CREATE TABLE energy_sources (
    source_id INT PRIMARY KEY,
    source_name VARCHAR(255)
);
