tables.table_schema:
CREATE TABLE packaging_materials (
    id UUID PRIMARY KEY,
    material_name STRING,
    recycling_score INT,
    environmental_impact STRING
);

CREATE TABLE packaging_products (
    id UUID PRIMARY KEY,
    product_name STRING,
    packaging_material_id UUID REFERENCES packaging_materials(id),
    usage INT
);

CREATE TABLE packaging_solution (
    id INT PRIMARY KEY,
    solution_name VARCHAR(50) NOT NULL,
    material_type VARCHAR(50) NOT NULL,
    sustainability_rating INT
);

CREATE TABLE packaging_lifecycle (
    id INT PRIMARY KEY,
    package_id INT NOT NULL,
    lifecycle_stage VARCHAR(50) NOT NULL,
    duration_days INT
);

CREATE TABLE packaging_material (
    id INT PRIMARY KEY,
    material_id INT NOT NULL,
    material_name VARCHAR(50) NOT NULL,
    supplier VARCHAR(50) NOT NULL,
    price DECIMAL
);

CREATE TABLE packaging_impact (
    id INT PRIMARY KEY,
    package_id INT NOT NULL,
    impact_type VARCHAR(50) NOT NULL,
    impact_value DECIMAL
);

CREATE TABLE packaging_solutions ( 
    id UUID PRIMARY KEY, 
    packaging_type VARCHAR(50), 
    material_type VARCHAR(50), 
    lifecycle_stage VARCHAR(50) 
);

CREATE TABLE packaging_manufacturers ( 
    manufacturer_id UUID PRIMARY KEY, 
    manufacturer_name VARCHAR(100), 
    location VARCHAR(100), 
    contact_person VARCHAR(50) 
);

CREATE TABLE recycling_centers ( 
    center_id UUID PRIMARY KEY, 
    center_name VARCHAR(100), 
    location VARCHAR(100), 
    contact_person VARCHAR(50) 
);

CREATE TABLE packaging_history ( 
    history_id UUID PRIMARY KEY, 
    packaging_id UUID REFERENCES packaging_solutions(id), 
    action_date DATE, 
    action_description TEXT 
);
