tables.table_schema:
CREATE TABLE packaging_materials (
  material_id INT PRIMARY KEY,
  material_name VARCHAR(255),
  certification_status VARCHAR(50)
);

CREATE TABLE packaging_materials (
    id INT PRIMARY KEY,
    material_name VARCHAR(255) NOT NULL,
    certification_status VARCHAR(50) NOT NULL
);

CREATE TABLE certifications (
    id INT PRIMARY KEY,
    certification_name VARCHAR(255) NOT NULL,
    issuing_authority VARCHAR(255) NOT NULL
);

CREATE TABLE packaging_materials (
   material_id UUID PRIMARY KEY,
   material_name VARCHAR(255) NOT NULL,
   certification_status VARCHAR(50) NOT NULL
);

CREATE TABLE certification_agencies (
   agency_id UUID PRIMARY KEY,
   agency_name VARCHAR(255) NOT NULL,
   location VARCHAR(100)
);

CREATE TABLE material_certifications (
   certification_id UUID PRIMARY KEY,
   material_id UUID REFERENCES packaging_materials(material_id),
   agency_id UUID REFERENCES certification_agencies(agency_id),
   certification_date DATE
);

CREATE TABLE sustainable_practices (
   practice_id UUID PRIMARY KEY,
   practice_name VARCHAR(255) NOT NULL,
   description TEXT
);

CREATE TABLE certification_status (
  certification_id INT PRIMARY KEY,
  material_name TEXT,
  certification_date DATE,
  certification_status BOOLEAN
);

CREATE TABLE sustainability_metrics (
  metric_id INT PRIMARY KEY,
  metric_name TEXT,
  metric_description TEXT
);

CREATE TABLE material_certifications (
  certification_id INT,
  material_id INT,
  CONSTRAINT fk_certification FOREIGN KEY (certification_id) REFERENCES certification_status(certification_id),
  CONSTRAINT fk_material FOREIGN KEY (material_id) REFERENCES packaging_materials(material_id)
);
