tables.table_schema:
CREATE TABLE beach_clean_up_sites (
    site_id INT PRIMARY KEY,
    site_name VARCHAR(50) NOT NULL,
    location VARCHAR(100) NOT NULL
);

CREATE TABLE plastic_waste (
    waste_id INT PRIMARY KEY,
    site_id INT NOT NULL,
    waste_type VARCHAR(50) NOT NULL,
    weight DECIMAL(10,2) NOT NULL,
    collected_date DATE NOT NULL,
    FOREIGN KEY (site_id) REFERENCES beach_clean_up_sites(site_id)
);

CREATE TABLE beach_clean_up_sites (
  site_id INT PRIMARY KEY,
  site_name VARCHAR(50) NOT NULL,
  location VARCHAR(100) NOT NULL
);

CREATE TABLE plastic_waste_data (
  waste_id INT PRIMARY KEY,
  site_id INT,
  waste_type VARCHAR(50) NOT NULL,
  amount_collected DECIMAL(10,2) NOT NULL,
  collection_date DATE NOT NULL,
  FOREIGN KEY (site_id) REFERENCES beach_clean_up_sites(site_id)
);

CREATE TABLE users (
  user_id INT PRIMARY KEY,
  username VARCHAR(50) UNIQUE NOT NULL,
  email VARCHAR(100) NOT NULL
);

CREATE TABLE BeachCleanupSites (
  site_id INT PRIMARY KEY,
