tables.table_schema:
CREATE TABLE plastic_recycling (
  id INT PRIMARY KEY,
  date DATE,
  total_bottles INT,
  recycled_bottles INT
);

CREATE TABLE PlasticBottles (
    bottle_id INT PRIMARY KEY,
    recycling_status VARCHAR(10),
    weight_kg DECIMAL
);

CREATE TABLE RecyclingStatistics (
    stats_id INT PRIMARY KEY,
    date DATE,
    recycled_count INT,
    landfill_count INT
);

CREATE TABLE PlasticBottles (
  bottle_id INT PRIMARY KEY,
  brand VARCHAR(50),
  recycled BOOLEAN,
  landfill BOOLEAN
);

CREATE TABLE RecyclingCenters (
  center_id INT PRIMARY KEY,
  location VARCHAR(100),
  total_bottles_recycled INT,
  total_bottles_received INT
);

CREATE TABLE Landfills (
  landfill_id INT PRIMARY KEY,
  location VARCHAR(100),
  bottles_received INT,
  bottles_not_recycled INT
);

CREATE TABLE plastic_bottles (
   id INT PRIMARY KEY,
   bottle_type VARCHAR(50),
   recycle_status BOOLEAN
);

CREATE TABLE landfill_sites (
   site_id INT PRIMARY KEY,
   site_name VARCHAR(100),
   landfill_capacity INT
);

CREATE TABLE recycling_centers (
   center_id INT PRIMARY KEY,
   center_name VARCHAR(100),
   center_location VARCHAR(255)
);

CREATE TABLE recycling_stats (
   stat_id INT PRIMARY KEY,
