tables.table_schema:
CREATE TABLE plastic_manufacturing_companies (
    company_id INT PRIMARY KEY,
    company_name VARCHAR(255),
    waste_management_practices VARCHAR(255)
);

CREATE TABLE plastic_companies (
    company_id INT PRIMARY KEY,
    company_name VARCHAR(50) NOT NULL,
    location VARCHAR(100) NOT NULL,
    waste_type VARCHAR(50) NOT NULL
);

CREATE TABLE Plastic_Companies (
    company_id INT PRIMARY KEY,
    company_name VARCHAR(255),
    location VARCHAR(255),
    waste_production DECIMAL
);

CREATE TABLE Recycling_Centers (
    center_id INT PRIMARY KEY,
    center_name VARCHAR(255),
    location VARCHAR(255),
    accepted_materials VARCHAR(255)
);

CREATE TABLE Environmental_Impact (
    impact_id INT PRIMARY KEY,
    company_id INT,
    impact_type VARCHAR(50),
    impact_rating INT
);
