tables.table_schema:
CREATE TABLE plastic_recycling_facilities (
  id INT PRIMARY KEY,
  facility_name TEXT,
  location TEXT,
  capacity INT
);

CREATE TABLE processing_capacities (
  facility_id INT,
  plastic_type TEXT,
  capacity INT,
  FOREIGN KEY (facility_id) REFERENCES plastic_recycling_facilities(id)
);

CREATE TABLE plastic_recycling_facilities (
    facility_id INT PRIMARY KEY,
    facility_name VARCHAR(100),
    location VARCHAR(100),
    capacity_in_tons INT
);

CREATE TABLE plastic_recycling_facilities (
    facility_id UUID PRIMARY KEY,
    facility_name VARCHAR(100),
    location VARCHAR(255),
    processing_capacity INT
);

CREATE TABLE plastic_materials (
    material_id UUID PRIMARY KEY,
    material_type VARCHAR(50),
    recyclability_rating INT
);

CREATE TABLE facility_inventory (
    inventory_id UUID PRIMARY KEY,
    facility_id UUID,
    material_id UUID,
    quantity INT
);

CREATE TABLE plastic_recycling_facility (
  facility_id INT PRIMARY KEY,
  facility_name VARCHAR(50),
  location VARCHAR(100),
  capacity_kg_per_hour INT
);

CREATE TABLE plastic_collection_point (
  point_id INT PRIMARY KEY,
  point_name VARCHAR(50),
  location VARCHAR(100),
  capacity_kg INT
);
